/**
 * NIP-66: Relay Discovery and Liveness Monitoring
 *
 * Implements utilities for creating and parsing relay discovery events
 * (kind 30166) and relay monitor announcement events (kind 10166).
 *
 * @see https://github.com/nostr-protocol/nips/blob/master/66.md
 */
import { NostrEvent } from "../types/nostr";
import { UnsignedEvent } from "../nip01/event";
import { RelayDiscoveryEventOptions, ParsedRelayDiscoveryEvent, RelayMonitorAnnouncementOptions, ParsedRelayMonitorAnnouncement } from "./types";
export * from "./types";
/** Constant for relay discovery event kind */
export declare const RELAY_DISCOVERY_KIND = 30166;
/** Constant for monitor announcement kind */
export declare const RELAY_MONITOR_KIND = 10166;
/**
 * Create a relay discovery event (kind 30166)
 */
export declare function createRelayDiscoveryEvent(options: RelayDiscoveryEventOptions, pubkey: string): UnsignedEvent;
/**
 * Parse a relay discovery event into a structured object
 */
export declare function parseRelayDiscoveryEvent(event: NostrEvent): ParsedRelayDiscoveryEvent | null;
/**
 * Create a relay monitor announcement event (kind 10166)
 */
export declare function createRelayMonitorAnnouncement(options: RelayMonitorAnnouncementOptions, pubkey: string): UnsignedEvent;
/**
 * Parse a relay monitor announcement event
 */
export declare function parseRelayMonitorAnnouncement(event: NostrEvent): ParsedRelayMonitorAnnouncement | null;
